// baekjoon input template
// prettier-ignore
{var fs=require('fs');var filePath=process.platform==='linux'?'/dev/stdin':'./input';var stdin=fs.readFileSync(filePath).toString().trim().split('\n').map(s=>s.trim());var input=(()=>{var line=0;return()=>stdin[line++]})();var SI=()=>input();var NI=()=>parseInt(input());var SAI=(sep=' ')=>input().split(sep);var NAI=(sep=' ')=>input().split(sep).map(Number);var CA=(n,v=null)=>Array(n).fill(v);var CAA=(n,m,v=null)=>Array.from(Array(n),()=>Array(m).fill(v));var CAAA=(n,m,h,v=null)=>Array.from(Array(n),()=>Array.from(Array(m),()=>Array(h).fill(v)));var P=console.log;}

const [N, M] = NAI();

const box = [];

CA(N).forEach(() => {
  box.push(NAI());
});

const DIR = {
  UP: 0,
  DOWN: 1,
  LEFT: 2,
  RIGHT: 3,
};

const dy = [-1, 1, 0, 0]; // 상 하 좌 우
const dx = [0, 0, -1, 1];

const curToNextDir = new Map([
  [DIR.UP, DIR.RIGHT],
  [DIR.DOWN, DIR.LEFT],
  [DIR.LEFT, DIR.DOWN],
  [DIR.RIGHT, DIR.UP],
]);

const yxdToNum = new Map();

let num = 1;

for (let y = 0; y < N; y++) {
  yxdToNum.set(`${y}|${0}|${DIR.LEFT}`, num);

  num++;
}

for (let x = 0; x < M; x++) {
  yxdToNum.set(`${N - 1}|${x}|${DIR.DOWN}`, num);

  num++;
}

for (let y = N - 1; y >= 0; y--) {
  yxdToNum.set(`${y}|${M - 1}|${DIR.RIGHT}`, num);

  num++;
}

for (let x = M - 1; x >= 0; x--) {
  yxdToNum.set(`${0}|${x}|${DIR.UP}`, num);

  num++;
}

const move = (d, y, x) => {
  let cy = y;
  let cx = x;
  let cd = d;

  while (true) {
    cd = box[cy][cx] ? curToNextDir.get(cd) : cd;

    const ny = cy + dy[cd];
    const nx = cx + dx[cd];

    if (ny < 0 || ny >= N || nx < 0 || nx >= M) {
      break;
    }

    cy = ny;
    cx = nx;
  }

  return yxdToNum.get(`${cy}|${cx}|${cd}`);
};

const ans = [];

num = 1;

for (let y = 0; y < N; y++) {
  ans.push(move(DIR.RIGHT, y, 0));

  num++;
}

for (let x = 0; x < M; x++) {
  ans.push(move(DIR.UP, N - 1, x));

  num++;
}

for (let y = N - 1; y >= 0; y--) {
  ans.push(move(DIR.LEFT, y, M - 1));

  num++;
}

for (let x = M - 1; x >= 0; x--) {
  ans.push(move(DIR.DOWN, 0, x));

  num++;
}

P(ans.join(' '));
